import React, { useEffect, useState } from 'react'
import axios from 'axios';
import { useParams, Link, useHistory } from "react-router-dom";
import AdoptButton from '../components/Buttons/AdoptButton';
import EditButton from '../components/Buttons/EditButton';
import HomeButton from '../components/Buttons/HomeButton';
import LikeButton from '../components/Buttons/LikeButton';

const Detail = (props) => {
    const [pet, setPet] = useState({})
    const { id } = useParams();
    const history = useHistory();
    const { removeFromDom } = props;

    useEffect(() => {
        axios.get('http://localhost:8000/api/pets/' +id)
            .then(res => setPet(res.data))
            .catch(err => console.error(err));
    }, []);

    // const like = () => {
    //     axios.put('http://localhost:8000/api/pets/' + id, {
    //         likes: pet.likes + 1
    //     })
    //     .then(res => setPet(res.data))
    //     .catch(err => console.error(err));
    // }

    return (
        <div>
            <HomeButton />
            <h1>Details about: {pet.name}</h1>
            <AdoptButton petId={pet._id} petName={pet.name} successCallback={()=>history.push('/')}/>
            <div>
                <table className="table">
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Type</th>
                            <th>Description</th>
                            <th>Skills</th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                            <td>{pet.name}</td>
                            <td>{pet.type}</td>
                            <td>{pet.description}</td>
                            <td>
                                <ul>
                                    <li>{pet.skill1}</li>
                                    <li>{pet.skill2}</li>
                                    <li>{pet.skill3}</li>
                                </ul>
                            </td>
                        </tr>
                    </tbody>
                </table>
                {/* <button class="btn btn-success" onClick={like}>üëç Like {pet.name}</button> */}
                <LikeButton petId={pet._id} petLikes={pet.likes} petName={pet.name} successCallback={()=>history.push('/')}/>
                {/* <p>{pet.likes} Like(s)</p> */}
            </div>
        </div>
    )
}

export default Detail;
