import React, { useEffect, useState } from 'react'
import axios from 'axios';
import { useParams, Link, useHistory } from "react-router-dom";
import AdoptButton from '../components/Buttons/AdoptButton';
import EditButton from '../components/Buttons/EditButton';
import HomeButton from '../components/Buttons/HomeButton';

const Detail = (props) => {
    const [pet, setPet] = useState({})
    const { id } = useParams();
    const history = useHistory();
    const { removeFromDom } = props;

    useEffect(() => {
        axios.get('http://localhost:8000/api/pets/' +id)
            .then(res => setPet(res.data))
            .catch(err => console.error(err));
    }, []);

    //implement a likes button that increases count by 1 and updates the database
    //disable the like button until the page reloads

    const like = () => {
        axios.put('http://localhost:8000/api/pets/' + id, {
            likes: pet.likes + 1
        })
        .then(res => setPet(res.data))
        .catch(err => console.error(err));
    }



    return (
        <div>
            <HomeButton />
            <h1>Details about: {pet.name}</h1>
            <div>
                //render the pet's details in a cascading table
                <table className="table">
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Type</th>
                            <th>Description</th>
                            <th>Skills</th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                            <td>{pet.name}</td>
                            <td>{pet.type}</td>
                            <td>{pet.description}</td>
                            <td>
                                <ul>
                                    <li>{pet.skill1}</li>
                                    <li>{pet.skill2}</li>
                                    <li>{pet.skill3}</li>
                                </ul>
                            </td>
                        </tr>
                    </tbody>
                </table>
                <button onClick={like}>üëç Like {pet.name}</button>
                <p>{pet.likes} Like(s)</p>

            </div>
            <AdoptButton petId={pet._id} successCallback={()=>history.push('/')}/>

        </div>
    )
}

export default Detail;
